// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC721/extensions/IERC721Metadata.sol";

contract RogueLand {
    address public owner;
    address public nftAddress;
	
	  struct Player {
      uint x;
      uint y;
      uint rewards;
      uint nextAction;
    }
  
    struct Action {
      address player;
      uint runs;
      uint actionType;
      uint playerNextAction;
      uint nextActionToExecute;
    }
  
    struct Gold {
      uint quantity;
      uint totalMiner;
      uint x;
      uint y;
    }
  
    struct Authorizer {
      uint id;
      string name;
      address holder;
    }

    struct PunkInfo {
      uint id;
      string name;
      string uri;
    }
  
    // 
    mapping (address => Authorizer) public authorizerOf;
  
    // 
    mapping (address => Player) public player;
  
    // 
    mapping (address => Action) public currentActionOf;
  
    // id
    mapping (uint => Action) public action;
  
    // 
    Gold[6] gold;
  
    constructor(address nftAddress_) {
        owner = msg.sender;
        nftAddress = nftAddress_;
    }

    // punk
    function authorize(address player_, uint id_, string memory name_) public {
        IERC721 nft = IERC721(nftAddress);
        require(msg.sender == nft.ownerOf(id_), "Only owner can authorize his punk!");
        authorizerOf[player_] = Authorizer(id_, name_, msg.sender);
    }

    function getAuthorizedId(address player_) public view returns (int) {
      IERC721 nft = IERC721(nftAddress);
      if (authorizerOf[player_].holder == address(0) || authorizerOf[player_].holder != nft.ownerOf(authorizerOf[player_].id)) {
        return -1;
      }
      else {
        return int(authorizerOf[player_].id);
      }
    }

    function getPunkInfo(address player_) public view returns (PunkInfo memory) {
      require (getAuthorizedId(player_) >= 0, "not authorized");
      IERC721Metadata nft = IERC721Metadata(nftAddress);
      uint id = authorizerOf[player_].id;
      string memory name = authorizerOf[player_].name;
      string memory uri = nft.tokenURI(id);
      return PunkInfo(id, name, uri);
    }

}
